INSERT INTO public.entity_db (entity_number, entity_name, entity_class, entity_type, entity_description, capture_date, team_nickname, escaped_status, capture_location_id, ghostbusters_station, storage_location, status, created_at)
VALUES
(1001, 'Le Fantôme de la Bibliothèque', 4, 3, 'L''esprit d''une vieille bibliothécaire qui hante la bibliothèque de New York, terrorisant les visiteurs.', '1984-03-15', 'Bibliothèque Squad', true, false, 1, 'New York', 'New York', 'captured', now()),
(1002, 'Slimer', 5, 14, 'Un fantôme vert et glouton connu pour son appétit insatiable.', '2014-06-14', 'Bouffetout', true, true, 2, 'New York', 'New York', 'free', now()),
(1003, 'Gozer le Gozerien', 7, 5, 'Un ancien démon capable de prendre différentes formes pour détruire des mondes. Gozer est une entité extrêmement dangereuse.', '1984-10-31', 'Le Destructeur', true, false, 4, 'New York', 'New York', 'captured', now()),
(1004, 'Stay Puft Marshmallow Man', 7, 1, 'Une énorme créature en guimauve invoquée par Gozer. Malgré son apparence joviale, il est extrêmement dangereux.', '1984-11-01', 'Le ptit marin', true, false, 5, 'New York', 'New York', 'captured', now()),
(1005, 'Vigo les Carpates', 7, 2, 'Un ancien sorcier et tyran piégé dans un tableau, essayant de renaître à travers une possession. Sa puissance est terrifiante.', '1989-12-31', 'Le Prince des Ténèbres', true, false, 1, 'New York', 'New York', 'captured', now()),
(1006, 'Lady Agatha', 5, 14, 'La version féminine de Slimer, plus malicieuse et toujours affamée. Elle est plus difficile à capturer que son homologue masculin.', '2016-07-05', 'Madame Bouffetout', false, false, 2, 'New York', 'New York', 'free', now()),
(1007, 'Le Frappeur du Wall Street Journal', 5, 13, 'Cet esprit frappeur perturbe les bureaux, renversant des piles de documents et effrayant les traders.', '2012-09-15', 'Le Broker Fou', false, false, 3, 'New York', 'New York', 'free', now()),
(1008, 'L''Hurleur du Parc', 3, 11, 'Un esprit enragé qui hante les parcs la nuit, criant sans cesse et provoquant des pannes d''électricité.', '2005-08-13', 'Le Criard', true, false, 4, 'New York', 'New York', 'captured', now()),
(1009, 'L''Ombre d''Arnold le Bouteux', 6, 19, 'Un ancien criminel boiteux qui apparaît dans les ruelles sombres, semant la terreur parmi les passants.', '1998-10-10', 'Le Botteur d''Ombres', false, true, 5, 'New York', 'New York', 'free', now()),
(1010, 'Lady Abigail la Torturée', 4, 6, 'Une dame de la haute société, assassinée lors d''une soirée, hante les grands hôtels en cherchant son meurtrier.', '1995-12-24', 'La Dame en Détresse', true, false, 6, 'New York', 'New York', 'captured', now()),
(1011, 'Le Phantom du Wagon 13', 5, 15, 'Un ancien conducteur de train, il hante le dernier wagon de tous les trains, cherchant à finir son dernier voyage.', '1993-01-07', 'Le Railleur', false, false, 7, 'New York', 'New York', 'free', now()),
(1012, 'L''Esprit de la Tour Sombre', 6, 19, 'Gardien maudit d''une ancienne tour, il protège les secrets qu''elle abrite. Sa présence est annoncée par des bruits de chaînes.', '1987-04-18', 'Le Gardien Silencieux', true, false, 1, 'New York', 'New York', 'captured', now()),
(1013, 'Le Veilleur du Lac Noir', 3, 8, 'Un pêcheur disparu, il hante les rives du lac, attirant les bateaux dans les eaux sombres.', '1999-06-21', 'L''Appel du Lac', false, false, 2, 'New York', 'New York', 'free', now()),
(1014, 'La Veuve Blanche des Champs', 4, 4, 'Une femme en pleurs aperçue dans les champs la nuit, cherchant son mari disparu à la guerre.', '2002-07-17', 'La Mère en Deuil', true, false, 3, 'New York', 'New York', 'captured', now()),
(1015, 'Le Garde du Cimetière Sans Nom', 5, 12, 'Gardien des tombes sans nom, il veille sur les âmes oubliées des cimetières.', '1986-11-03', 'Le Protecteur Silencieux', false, false, 4, 'New York', 'New York', 'free', now()),
(1016, 'L''Écolier Fantôme', 2, 13, 'Un jeune garçon décédé, il hante les salles de classe, jouant des farces aux élèves et aux professeurs.', '2000-02-12', 'Le Farceur Scolaire', true, false, 5, 'New York', 'New York', 'captured', now()),
(1017, 'Le Marin Sans Repos', 5, 9, 'Un marin disparu en mer, il hante les quais, appelant les navires qui n''arrivent jamais.', '1991-09-09', 'Le Nautilus', false, true, 6, 'New York', 'New York', 'free', now()),
(1018, 'Le Joueur de Flûte de Minuit', 4, 17, 'Un musicien maudit, sa mélodie attire les âmes perdues et les entraîne dans l''oubli.', '1988-12-14', 'Le Mélodiste Maudit', true, false, 7, 'New York', 'New York', 'captured', now()),
(1019, 'Le Charpentier Hanté', 6, 11, 'Ancien charpentier accusé à tort, il hante les maisons en construction, causant des accidents inexplicables.', '1994-11-25', 'Le Cloueur Fou', false, false, 1, 'New York', 'New York', 'free', now()),
(1020, 'La Comtesse Sanglante', 7, 5, 'Une aristocrate cruelle, transformée en démon, elle hante les manoirs, assoiffée de vengeance.', '2001-10-30', 'La Dame de Sang', true, false, 2, 'New York', 'New York', 'captured', now()),
(1021, 'Le Prisonnier de la Cellule 7', 5, 19, 'Un prisonnier condamné à tort, il hante sa cellule, cherchant à prouver son innocence.', '1990-06-19', 'Le Détenu Fantôme', false, false, 3, 'New York', 'New York', 'free', now()),
(1022, 'L''Apothicaire Fou', 3, 13, 'Un ancien apothicaire, il hante les pharmacies, renversant les fioles et perturbant les préparations.', '1985-10-15', 'Le Remède Mortel', true, false, 4, 'New York', 'New York', 'captured', now()),
(1023, 'Le Fantôme de la Chaise', 5, 13, 'Un poltergeist espiègle qui aime faire bouger les meubles, surtout les chaises, effrayant ceux qui osent s''asseoir.', '1993-07-18', 'Le Déplaceur', false, false, 5, 'New York', 'New York', 'free', now()),
(1024, 'L''Esprit des Sables', 6, 5, 'Un esprit maléfique emprisonné dans un ancien artefact égyptien, connu pour contrôler des tempêtes de sable.', '1991-05-12', 'Le Désert Furieux', true, false, 6, 'New York', 'New York', 'captured', now()),
(1025, 'La Dame de Glace', 4, 18, 'Un esprit froid qui fige tout sur son passage. Elle est souvent aperçue dans les ruelles gelées les nuits d''hiver.', '2003-01-21', 'La Gelée', false, false, 7, 'New York', 'New York', 'free', now()),
(1026, 'Le Bûcheron Hanté', 3, 11, 'Un ancien bûcheron décédé lors d''un accident tragique. Il hante les forêts et attaque les bûcherons qui s''y aventurent.', '1998-03-02', 'L''Abatteur', true, false, 1, 'New York', 'New York', 'captured', now()),
(1027, 'Le Siffleur du Métro', 2, 13, 'Un esprit joueur qui aime siffler dans les tunnels du métro, effrayant les passagers et les conducteurs.', '2005-08-12', 'Le Siffleur', false, false, 2, 'New York', 'New York', 'free', now()),
(1028, 'Le Gardien du Musée', 4, 16, 'Un ancien gardien de musée, il veille sur les œuvres d''art la nuit, s''assurant qu''elles ne soient pas perturbées.', '2000-02-14', 'Le Curateur Fantôme', true, false, 3, 'New York', 'New York', 'captured', now()),
(1029, 'Le Clown Maudit', 6, 18, 'Un clown maléfique qui hante les vieux cirques abandonnés, semant la terreur avec ses rires sinistres.', '2006-04-15', 'Le Rieur Sanglant', true, false, 4, 'New York', 'New York', 'captured', now()),
(1030, 'L''Ombre du Miroir', 3, 9, 'Un esprit qui vit dans les miroirs, copiant les mouvements des vivants et les observant silencieusement.', '1994-10-24', 'Le Réflecteur', false, false, 5, 'New York', 'New York', 'free', now()),
