-- Dossier: database/data
-- Fichier: data_entities.sql

\c ghostbusters_db

-- 1. Définir l'encodage client à UTF-8
SET client_encoding TO 'UTF8';

-- 2. Définir le schéma à utiliser
SET search_path TO public;

-- 3. Insertion des types d'entités sans duplication
ON CONFLICT (type_label) DO UPDATE SET created_at = excluded.created_at;

\echo 'Seeding des types d''entités terminé avec succès.'

-- 4. Insertion des classes d'entités sans duplication
ON CONFLICT (class_label) DO UPDATE SET created_at = EXCLUDED.created_at;

\echo 'Seeding des classes d’entités terminé avec succès.'

-- 5. Insertion des lieux de capture sans duplication
ON CONFLICT (capture_location, city, latitude, longitude) DO UPDATE SET created_at = EXCLUDED.created_at;

\echo 'Seeding des lieux de capture terminé avec succès.'

-- 6. Insertion des équipes Ghostbusters sans duplication
ON CONFLICT (team_name) DO UPDATE SET created_at = excluded.created_at;

\echo 'Seeding des équipes Ghostbusters terminé avec succès.'

-- 7. Ajout d'une contrainte UNIQUE sur entity_name dans entity_db si elle n'existe pas déjà
DO $$
BEGIN
    IF NOT EXISTS (
        SELECT 1
        FROM pg_constraint
        WHERE conname = 'unique_entity_name' AND conrelid = 'public.entity_db'::regclass
    ) THEN
        ALTER TABLE public.entity_db ADD CONSTRAINT unique_entity_name UNIQUE (entity_name);
    END IF;
END $$ LANGUAGE plpgsql;

-- 8. Insertion des entités sans duplication
ON CONFLICT (entity_name) DO UPDATE SET created_at = excluded.created_at;

-- Insertion des attributs des entités sans duplication
ON CONFLICT (entity_number, attribute_name) DO UPDATE SET attribute_value = EXCLUDED.attribute_value;

\echo 'Seeding des attributs des entités terminé avec succès.'

\echo 'Fin du script de seeding des entités.'